org.obiba.opal.datashield.CustomRScriptM.script:
function(data) {
  if(is.atomic(data)) {
    if(length(data) <= 1) {
      "Vector too small."
    } else {
      base::summary(data);
    }
  } else if(is.recursive(data)) {
      base::summary.default(data);
  }
}

function(data) {
  if(inherits(data, 'lm')) {
   sum.lm <- stats::summary.lm(data)
   list(
     coefficients=sum.lm$coefficients,
     numsubs=length(sum.lm$residuals)
   )
 } else {
  NULL
 }
}
